    private function obtenerColumnaExcel($col = array(0 => 65), $num = 0) {
        $col[count($col) - 1] += $num;
        $var = 0;
        for ($x = (count($col) - 1); $x >= 0; $x--) {
            if ($col[$x] > 90) {
                $col[$x] = $col[$x] - 26;
                if ($x > 0) {
                    $col[$x - 1] ++;
                }
                $var++;
            } else {
                break;
            }
        }
        if ($var == count($col)) {
            $col[$var] = 65;
        }
        return $col;
    }

    private function columnaExcel($col = array(0 => 65)) {
        $letra = "";
        for ($x = 0; $x < count($col); $x++) {
            $letra .= chr($col[$x]);
        }
        return $letra;
    }

